const AdminSidePanel = () => {
    const [isClicked, setIsClicked] = useState<number | null>(null);

    const handleClick = (index: number) => {
        setIsClicked(index === isClicked ? null : index);
    };

    const [showModal, setShowModal] = useState<boolean>(false);
    const [phoneActive, setPhoneActive] = useState<boolean>(
        window.innerWidth < 1100
    );

    const resizeHandler = () => {
        setPhoneActive(window.innerWidth < 1200);
    };

    useEffect(() => {
        window.addEventListener("resize", resizeHandler);
        return () => {
            window.removeEventListener("resize", resizeHandler);
        };
    }, []);

    return (
        <>
            {phoneActive && (
                <button id="hamburger" onClick={() => setShowModal(!showModal)}>
                    <HiMenuAlt4 />
                </button>
            )}
            <div className="adminSidePanel">
                <div
                    style={
                        phoneActive
                            ? {
                                  width: "240px",
                                  position: "fixed",
                                  top: 160,
                                  left: showModal ? "0" : "-20rem",
                                  transition: "all 0.5s",
                                  zIndex: 1,
                              }
                            : {}
                    }>
                    {sidebarData.map((item, index) => (
                        <button
                            key={item.name}
                            className={`panelBox ${
                                index === isClicked ? "activePanelBox" : ""
                            }`}
                            onClick={() => {
                                handleClick(index);
                                setShowModal(false);
                            }}>
                            {index === isClicked ? item.iconActive : item.icon}
                            <span>{item.name}</span>
                            {index === isClicked && <div></div>}
                        </button>
                    ))}
                </div>
            </div>
        </>
    );
};

export default AdminSidePanel;
